for(var backLink=document.querySelectorAll(".buy"),backPopup=document.querySelector(".modal-back"),backClose=backPopup.querySelector(".modal-close"),i=0;i<backLink.length;i+=1)backLink[i].addEventListener("click",function(e){e.preventDefault(),backPopup.classList.add("modal-back-show")});backClose.addEventListener("click",function(e){e.preventDefault(),backPopup.classList.remove("modal-back-show")});
